mapper.update:
update
record_user_id = #{recordUserId,jdbcType=VARCHAR}, record_title = #{recordTitle,jdbcType=VARCHAR}, record_view_count = #{recordViewCount,jdbcType=INTEGER}, record_comment_count = #{recordCommentCount,jdbcType=INTEGER}, record_like_count = #{recordLikeCount,jdbcType=INTEGER}, record_create_time = #{recordCreateTime,jdbcType=VARCHAR}, record_update_time = #{recordUpdateTime,jdbcType=VARCHAR}, record_is_comment = #{recordIsComment,jdbcType=INTEGER}, record_status = #{recordStatus,jdbcType=INTEGER}, record_order = #{recordOrder,jdbcType=INTEGER}, record_content = #{recordContent,jdbcType=LONGVARCHAR}, record_summary = #{recordSummary,jdbcType=VARCHAR},
where record_id = #{recordId,jdbcType=INTEGER}

mapper.select:
SELECT
record.*
FROM
record



            AND record.record_status = #{status}


            AND record.record_title LIKE concat(concat('%',#{keywords}),'%')


            AND record.record_user_id = #{userId}


            AND record.record_id IN (
            SELECT record_category_ref.record_id FROM record_category_ref
            WHERE record_category_ref.category_id = #{categoryId}
            )

SELECT record.* FROM record
record.record_status = #{status} AND
record.record_id = #{id}
